import os from 'os'

export const create_schema = name => {
	let schema = '{\n'
	schema += '\t"$schema": "https://json-schema.org/draft/2019-09/schema",\n'
	schema += `\t"title": "${name}",\n`
	schema += '\t"type": "object",\n'
	schema += '\t"properties": {}\n'
	schema += '}' + os.EOL

	return schema
}
